import React from 'react'
import hoistNonReactStatics from 'hoist-non-react-statics'

const Context = React.createContext({})

export default Context

class SafeRender extends React.Component {
  shouldComponentUpdate (nextProps) {
    if (nextProps.filter) {
      nextProps.filter(this.state, nextProps.state, meta)
    }
    return true
  }
  render () {
    return children
  }
}

export const Connect = (subscribe, options) => Comp => {
  const subscriber = typeof subscribe({}) === 'function' ? subscribe() : subscribe
  class Connected extends React.Component {
    render () {
      filter: (oldState, newState, meta) => meta.type !== 'pointer',
      return (
        <Context.Consumer>
          {({ dispatch, _meta, ...rest }) => (
            <Comp {...subscriber(rest, this.props)} {...this.props} dispatch={dispatch} />
          )}
        </Context.Consumer>
      )
    }
  }
  hoistNonReactStatics(Connected, Comp)
  return Connected
}
