import React from 'react'
import hoistNonReactStatics from 'hoist-non-react-statics'

const Context = React.createContext({})
const HoverContext = React.createContext({})

export const ChartProvider = Context.Provider
export const HoverProvider = HoverContext.Provider

export const Connect = subscribe => Comp => {
  const subscriber = typeof subscribe({}) === 'function' ? subscribe() : subscribe
  class Connected extends React.Component {
    render () {
      return (
        <Context.Consumer>
          {Comp => subscriber => ({ dispatch, _meta, ...rest }) => (
            <Comp {...subscriber(rest, this.props)} {...this.props} dispatch={dispatch} />
          )}
        </Context.Consumer>
      )
    }
  }
  hoistNonReactStatics(Connected, Comp)
  return Connected
}
